const outputItems = []; // This array will hold the chunks we want to output
const chunkSize = 100;

// Step 1: Collect all the 'json' data objects from the incoming n8n items
// The 'items' variable holds an array of n8n's internal item objects.
// We want to extract the 'json' property from each of them.
const allRawRecords = items.map(item => item.json);

if (!Array.isArray(allRawRecords)) {
    // This case should be rare if the previous node is structured correctly,
    // but it's good for robustness.
    return [{ json: { error: "Input data is not an array of raw records.", receivedData: allRawRecords } }];
}

console.log(`Total records to chunk: ${allRawRecords.length}`);

// Step 2: Iterate through the collected raw records and create chunks
for (let i = 0; i < allRawRecords.length; i += chunkSize) {
    const chunk = allRawRecords.slice(i, i + chunkSize);
    const chunkNumber = Math.floor(i / chunkSize) + 1;

    console.log(`Prepared chunk ${chunkNumber} with ${chunk.length} records.`);

    // Each object pushed to outputItems will become a separate n8n item.
    // Here, each item will contain a 'chunk' property holding the array of records for that block.
    outputItems.push({ json: { chunk: chunk } }); 

    // Optional: Add a small delay if sending these chunks to an external service
    // right after this node might hit rate limits.
    // await new Promise(resolve => setTimeout(resolve, 500)); 
}

// Step 3: Return the array of n8n items, where each item contains a chunk.
return outputItems;

// Paso 1: Extraer y limpiar el contenido del campo "output"
const rawOutput = $json.output || "";
const cleanedJsonText = rawOutput.replace(/```json|```/g, "").trim();

let parsed;
try {
  parsed = JSON.parse(cleanedJsonText);
} catch (error) {
  throw new Error("No se pudo parsear el JSON del campo output.");
}

// Paso 2: Extraer los campos del JSON (sin recomendaciones)
const { alerta, resumen, noticiasRelevantes } = parsed;

