`;

// Paso 5: Retornar sujeto y mensaje como campos separados
return [
  {
    json: {
      sujeto,
      mensaje
    }
  }
];

let resumenes = [];
let noticiasTotales = [];

// Iterar por todos los items generados por el Agente IA
for (const item of items) {
  try {
    // Eliminar backticks y etiquetas de markdown
    const limpio = item.json.output.replace(/```json|```/g, "").trim();
    const data = JSON.parse(limpio);

    resumenes.push({
      id: data.bloqueId,
      texto: data.resumen
    });

    noticiasTotales = noticiasTotales.concat(data.noticias || []);
  } catch (e) {
    throw new Error(`Error al procesar un bloque: ${e.message}`);
  }
}

// Ordenar por bloqueId
resumenes.sort((a, b) => a.id - b.id);

// Concatenar los res√∫menes en un solo texto
const resumenCompleto = resumenes.map(r => `Bloque ${r.id}: ${r.texto}`).join('\n\n');

return [
  {
    json: {
      resumenCompleto,
      noticiasRelevantes: noticiasTotales
    }
  }
];
